// #Sireum

package bc.BoilerControl

import org.sireum._
import art.Art
import bc._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun
@msig trait WaterMeter_i_bcproc_waterMeter_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter)
  }

  /** helper function to set the values of all input ports.
   * @param opMode payloads for event data port opMode.
   *   ART currently supports single element event data queues so
   *   only the last element of opMode will be used
   * @param programReady the number of events to place in the programReady event port queue.
   *   ART currently supports single element event queues so at most
   *   one event will be placed in the queue.
   */
  def put_concrete_inputs(opMode : ISZ[BoilerControl.OpMode.Type],
                          programReady : Z): Unit = {
    for(v <- opMode){
      put_opMode(v)
    }
    for(i <- 0 until programReady) {
      put_programReady()
    }
  }


  /** helper function to check WaterMeter_i_bcproc_waterMeter's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param level method that will be called with the payloads to be sent
   *        on the outgoing event data port 'level'.
   * @param ready method that will be called with the number of events to be sent
   *        on the outgoing event port 'ready'.
   */
  def check_concrete_output(level: ISZ[Base_Types.Float_32] => B,
                            ready: Z => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var levelValue: ISZ[Base_Types.Float_32] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_level().nonEmpty) { levelValue = levelValue :+ get_level().get }
    if(!level(levelValue)) {
      testFailures = testFailures :+ st"'level' did not match expected: received ${levelValue.size} events with the following payloads ${levelValue}"
    }
    // TODO: event port getter should return the number of events in
    //       the output queue when queue sizes > 1 support is added to ART
    val readyValue: Z = if(get_ready().nonEmpty) z"1" else z"0"
    if(!ready(readyValue)) {
      testFailures = testFailures :+ st"'ready' did not match expected: ${readyValue} events were in the outgoing event queue"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_opMode(value : BoilerControl.OpMode.Type): Unit = {
    Art.insertInInfrastructurePort(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter.operational_api.opMode_Id, BoilerControl.OpMode_Payload(value))
  }

  // setter for in EventPort
  def put_programReady(): Unit = {
    Art.insertInInfrastructurePort(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter.operational_api.programReady_Id, art.Empty())
  }

  // getter for out EventDataPort
  def get_level(): Option[Base_Types.Float_32] = {
    val value: Option[Base_Types.Float_32] = get_level_payload() match {
      case Some(Base_Types.Float_32_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port level.  Expecting 'Base_Types.Float_32_Payload' but received ${v}")
      case _ => None[Base_Types.Float_32]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_level_payload(): Option[Base_Types.Float_32_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter.initialization_api.level_Id).asInstanceOf[Option[Base_Types.Float_32_Payload]]
  }

  // getter for out EventPort
  def get_ready(): Option[art.Empty] = {
    val value: Option[art.Empty] = get_ready_payload() match {
      case Some(art.Empty()) => Some(art.Empty())
      case Some(v) => halt(s"Unexpected payload on port ready.  Expecting 'art.Empty' but received ${v}")
      case _ => None[art.Empty]()
    }
    return value
  }

  // payload getter for out EventPort
  def get_ready_payload(): Option[art.Empty] = {
    return Art.observeOutInfrastructurePort(Arch.BoilerControlSystem_i_Instance_bcproc_waterMeter.initialization_api.ready_Id).asInstanceOf[Option[art.Empty]]
  }

}
